mapper.update:
	update authority_rel
		set 
			authority_id = CASE
			
			when user_id=(select user_id from user_info where id=#{item.ID}) then (select authority_id from authority where authority_name = #{item.Authority})
			
		End
	
	where 
		user_id in
		(select user_id from user_info where id in( 
			
				#{item.ID}	
			
	));
