mapper.select:
select COMMON_CODE_ID , NAME , ORDER_NUMBER , ATTRIBUTION_1 , ATTRIBUTION_2 , DESCRIPTION , SYSTEM_CODE_YN , USE_YN , PARENT_COMMON_CODE_ID , REGISTER_SEQ , REGISTRY_DATE , MODIFIER_SEQ , MODIFY_DATE from CIP_COMMON_CODE
and PARENT_COMMON_CODE_ID = #{PARENT_COMMON_CODE_ID}
and PARENT_COMMON_CODE_ID is null
and COMMON_CODE_ID = #{COMMON_CODE_ID}

mapper.insert:
    INSERT INTO CIP_COMMON_CODE(
		COMMON_CODE_ID 
		, NAME 
		, ORDER_NUMBER 
		, ATTRIBUTION_1 
		, ATTRIBUTION_2 
		, DESCRIPTION 
		, SYSTEM_CODE_YN 
		, USE_YN 
		, PARENT_COMMON_CODE_ID 
		, REGISTER_SEQ 
		, REGISTRY_DATE 
		, MODIFIER_SEQ 
		, MODIFY_DATE 
	)
	values(
		#{COMMON_CODE_ID} 
		, #{NAME} 
		, #{ORDER_NUMBER} 
		, #{ATTRIBUTION_1} 
		, #{ATTRIBUTION_2} 
		, #{DESCRIPTION} 
		, #{SYSTEM_CODE_YN} 
		, #{USE_YN} 
		, #{PARENT_COMMON_CODE_ID} 
		, #{REGISTER_SEQ} 
		, sysdate() 
		, #{REGISTER_SEQ} 
		, sysdate() 
	)
	ON DUPLICATE KEY UPDATE 
	NAME = #{NAME} 
	, ORDER_NUMBER = #{ORDER_NUMBER} 
	, ATTRIBUTION_1 = #{ATTRIBUTION_1} 
	, ATTRIBUTION_2 = #{ATTRIBUTION_2} 
	, DESCRIPTION = #{DESCRIPTION} 
	, SYSTEM_CODE_YN = #{SYSTEM_CODE_YN} 
	, USE_YN = #{USE_YN} 
	, PARENT_COMMON_CODE_ID = #{PARENT_COMMON_CODE_ID} 
	, MODIFIER_SEQ = #{MODIFIER_SEQ} 
	, MODIFY_DATE = sysdate()
