mapper.insert:
	    INSERT INTO CIP_MEMBER (
			MEMBER_SEQ 
			, MEMBER_ID 
			, PASSWORD 
			, CRYPT_PASSWORD	
			, NAME 
			, EMAIL 
			, CELLPHONE 
			, ORGANIZATION_SEQ 
			, REGISTER_SEQ 
			, REGISTRY_DATE 
			, MODIFIER_SEQ 
			, MODIFY_DATE 
		)
		values(
			#{MEMBER_SEQ} 
			, #{MEMBER_ID} 
			, #{PASSWORD} 
			, #{CRYPT_PASSWORD}	
			, #{NAME} 
			, #{EMAIL} 
			, #{CELLPHONE} 
			, #{ORGANIZATION_SEQ} 
			, #{REGISTER_SEQ} 
			, sysdate() 
			, #{REGISTER_SEQ} 
			, sysdate() 
		)
		ON DUPLICATE KEY UPDATE 
			MEMBER_ID = #{MEMBER_ID} 
			, PASSWORD = #{PASSWORD} 
			, CRYPT_PASSWORD = #{CRYPT_PASSWORD}	
			, NAME = #{NAME} 
			, EMAIL = #{EMAIL} 
			, CELLPHONE = #{CELLPHONE} 
			, ORGANIZATION_SEQ = #{ORGANIZATION_SEQ} 
			, MODIFIER_SEQ = #{MODIFIER_SEQ} 
			, MODIFY_DATE = sysdate()
