mapper.insert:
    INSERT INTO CIP_AUTHORITY (
		AUTHORITY_ID  
		, NAME 
		, DESCRIPTION 
		, USE_YN 
		, REGISTER_SEQ 
		, REGISTRY_DATE 
		, MODIFIER_SEQ 
		, MODIFY_DATE 
	)
	values(
		#{AUTHORITY_ID}  
		, #{NAME} 
		, #{DESCRIPTION} 
		, #{USE_YN} 
		, #{REGISTER_SEQ} 
		, sysdate() 
		, #{REGISTER_SEQ} 
		, sysdate() 
	)
	ON DUPLICATE KEY UPDATE 
		NAME = #{NAME} 
		, DESCRIPTION = #{DESCRIPTION} 
		, USE_YN = #{USE_YN} 
		, MODIFIER_SEQ = #{MODIFIER_SEQ} 
		, MODIFY_DATE = sysdate()
