mapper.insert:
		 
		SELECT MEMBER_SEQ
		FROM CIP_MEMBER
		WHERE MEMBER_SEQ = #{MEMBER_SEQ}
	
    

	MERGE INTO CIP_MEMBER CIP1
	     USING (SELECT #{MEMBER_SEQ} AS MEMBER_SEQ FROM DUAL) CIP2
	        ON (CIP1.MEMBER_SEQ = CIP2.MEMBER_SEQ)
	 WHEN MATCHED THEN
	    UPDATE SET
			MEMBER_ID = #{MEMBER_ID} 
			, PASSWORD = #{PASSWORD} 
			, NAME = #{NAME} 
			, EMAIL = #{EMAIL} 
			, CELLPHONE = #{CELLPHONE} 
			, ORGANIZATION_SEQ = #{ORGANIZATION_SEQ} 
			, MODIFIER_SEQ = #{MODIFIER_SEQ} 
			, MODIFY_DATE = sysdate 
	 WHEN NOT MATCHED THEN
	    INSERT (
			MEMBER_SEQ 
			, MEMBER_ID 
			, PASSWORD 
			, NAME 
			, EMAIL 
			, CELLPHONE 
			, ORGANIZATION_SEQ 
			, REGISTER_SEQ 
			, REGISTRY_DATE 
			, MODIFIER_SEQ 
			, MODIFY_DATE 
		)
		values(
			#{MEMBER_SEQ} 
			, #{MEMBER_ID} 
			, #{PASSWORD} 
			, #{NAME} 
			, #{EMAIL} 
			, #{CELLPHONE} 
			, #{ORGANIZATION_SEQ} 
			, #{REGISTER_SEQ} 
			, sysdate 
			, #{REGISTER_SEQ} 
			, sysdate 
		)

mapper.select:
select result.*
from (
select rownum as rn, member.*
from ( select * from CIP_MEMBER

order by NAME ) member

) result
where result.rn between #{pagination.pageBegin} and #{pagination.pageEnd}
